using System.Linq;
using System.Collections.Generic;

namespace Website.Models.DocumentModel
{
	public class DocumentParagraph
	{
		public IEnumerable<WebText>? TextParts;
		public string? String64Image;
		public string? ImageSubtext;


#if DEBUG
		static System.Random rnd = new System.Random();
		public static DocumentParagraph GenerateRandom()
		{
			var OutDoc = new DocumentParagraph
			{
				// Paragraph parts with random properties (italic, bold, linked)
				TextParts = new RandomDataGenerator.Randomizers.RandomizerTextLipsum(new RandomDataGenerator.FieldOptions.FieldOptionsTextLipsum { Paragraphs = 1 })
					.Generate().Split(' ').Select(x => WebText.GenerateRandomProps(x + ' ')),

				String64Image = rnd.Next(0, 2) < 0 ?
				//some random image
				@"data:image/png;base64,"
				: null
			};

			OutDoc.ImageSubtext = OutDoc.String64Image == null ? null : "This is imageSubtext";

			return OutDoc;
		}
#endif
	}
}
